import React from 'react';
import books from '../../books';
import { Dropdown, Row, Container, Col, Card, Button } from 'react-bootstrap';
class AllBooks extends React.Component
{
render()
{
    return(
        <>
{         books.filter(b => (b.category === this.props.selectedCategory)).map(b => <div style={{ display: 'inline', margin: '20px' }} key={b.id} ><Card style={{ width: '9rem', display: 'inline-block' }}>
                                    <Card.Img variant="top" src={b.cover} />
                                    <Card.Body style={{ width: '10rem' ,height:'10rem',   overflow: 'auto' }}>
                                        <Card.Title>{b.title}</Card.Title>
                                        <Card.Text>
                                            {b.author}
                                        </Card.Text>
                                    </Card.Body>
                                </Card></div>)}
        </>
    )

}
}
export default AllBooks;
