import React from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import './Categories.css';
import categories from '../../categories';
class Categories extends React.Component {
    selectedCategory=(e)=>
    {
console.log(e.target.value);
    }
    render() {
        let eventKey =1;
        return (
            <>
                <div className="sideMenu">

                    <Dropdown.Menu show>
                        <Dropdown.Header>Favorite Genres:</Dropdown.Header>
                        {
                            categories.map((c)=>{ eventKey=eventKey+1; return(<Dropdown.Item onClick={this.selectedCategory} eventKey={eventKey}>{c.Name}</Dropdown.Item>) })
                        }
                    </Dropdown.Menu>
                </div>
                <div className="categoriesSide">

                </div>
            </>
        )
    }
}
export default Categories;
