class AllBooks extends React.Component {
    render() {
        return (
            <>
                { books.filter(b => (b.category === this.props.selectedCategory)).map(b =>
                 <Book id={b.id} cover={b.cover} title={b.title} author={b.author} />
                
             )
            }
            </>
        )

    }
}
export default AllBooks;
