import React from 'react';
import { Card, ListGroup } from 'react-bootstrap';
import { MyContext } from '../../App';
import NavBar from '../Shared/Navbar';
import PaginationComponent from '../Shared/Pagination';
import BookCard from './BookCard';


class UserHome extends React.PureComponent {
    state={
      showedBooks:[],
    }
    handleClick=(e)=>
    {
          console.log(e.target)
    }
    render() {
        return (
            
            <MyContext.Consumer>

                {value =>
                    (
            <>
                <NavBar></NavBar>
                <div className='BooksListing'>
                   
                <ListGroup style={{ width: '18rem' }}>
                <ListGroup.Item action name='all' onClick={this.handleClick}>
                          All
                </ListGroup.Item>
                <ListGroup.Item action >
                     curently Reading
                 </ListGroup.Item>
                 <ListGroup.Item action >
                        Read
                 </ListGroup.Item>
                 <ListGroup.Item action >
                        want to read
                 </ListGroup.Item>
                </ListGroup>
                    <div style={{display:'flex',flexDirection:'column'}}>
                    <Card  style={{ width: '15rem', marginLeft:'20px' }}>
                        <Card.Body>All</Card.Body>
                    </Card>
                    <BookCard></BookCard>
                    <PaginationComponent></PaginationComponent>
                    </div>
                </div>
            </>
               )
            }
        </MyContext.Consumer>
        )
    }
}
export default UserHome;
