import React from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import UserNavBar from './UserNavBar';
import Table from 'react-bootstrap/Table'
import './Home.css';
import { MDBDataTable } from 'mdbreact';
import books from '../../books';

const data = {
    columns: [
        {
            label: 'Id',
            field: 'id',
            sort: 'asc',
            width: 150
        },
        {
            label: 'Cover',
            field: 'cover',
            sort: 'asc',
            width: 150
        },
        {
            label: 'Title',
            field: 'title',
            sort: 'asc',
            width: 270
        },
        {
            label: 'Author',
            field: 'author',
            sort: 'asc',
            width: 200
        },
        {
            label: 'Category',
            field: 'category',
            sort: 'asc',
            width: 100
        },
        {
            label: 'Rating',
            field: 'rating',
            sort: 'asc',
            width: 150
        },
        {
            label: 'Pages',
            field: 'pages',
            sort: 'asc',
            width: 100
        }
    ],
    rows: books.map(b=>{b.cover=<img src={b.cover}/>})

}

class UserHome extends React.Component {

    render() {

        return (
            <>
                <UserNavBar />

                <div className="sideMenu">
                    {/* <Dropdown.Menu show> */}
                    <Dropdown.Header>Bookshelves</Dropdown.Header>
                    <Dropdown.Item eventKey="2">All</Dropdown.Item>
                    <Dropdown.Item eventKey="3">Read</Dropdown.Item>
                    <Dropdown.Item eventKey="3">Currently Reading</Dropdown.Item>
                    <Dropdown.Item eventKey="3">Want To Read</Dropdown.Item>

                    {/* </Dropdown.Menu> */}
                    <div className="spliterline"></div>
                </div>
                <div className="rightside">
                    <MDBDataTable
                        // small
                        data={data}
                    />

                </div>

            </>
        )
    }
}
export default UserHome;
