import React from 'react';
import { Navbar } from 'react-bootstrap';
import { Form } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { Image } from 'react-bootstrap';
import { MyContext } from '../../App';
import Logo from '../../images/logo.png';

class SignIn extends React.PureComponent {

    state = {
        enteredMail: '',
        enteredPassword: '',
        enteredDataValidation: ''
    }
    handleSubmit = (existingUsers) => (event) => {
        event.preventDefault();
        debugger;
        for (var i = 0; i < existingUsers.length; i++) {
            if (existingUsers[i].password == this.state.enteredPassword && existingUsers[i].email == this.state.enteredMail) {
                this.setState({ enteredDataValidation: 'valid' })
                break;
            }
            else if (existingUsers[i].password == this.state.enteredPassword && existingUsers[i].email != this.state.enteredMail) {
                this.setState({ enteredDataValidation: 'email not valid' })
                break;
            }
            else if (existingUsers[i].password != this.state.enteredPassword && existingUsers[i].email == this.state.enteredMail) {
                this.setState({ enteredDataValidation: 'password not valid' })
                break;
            }
        }
        // existingUsers.map(user=>
        //     {
        //         // debugger;
        //         if(user.password==this.state.enteredPassword && user.email==this.state.enteredMail){
        //         this.setState({enteredDataValidation:'valid'})
        //         break;
        //         }
        //         else if(user.password==this.state.enteredPassword && user.email!=this.state.enteredMail){
        //         this.setState({enteredDataValidation:'email not valid'})
        //         break;
        //         }
        //         else if(user.password!=this.state.enteredPassword && user.email==this.state.enteredMail){
        //         this.setState({enteredDataValidation:'password not valid'})
        //         break;
        //         }
        //     })

        if (this.state.enteredDataValidation == '')
            this.setState({ enteredDataValidation: 'account not exist' })

    }
    handleEmailChange = (e) => {
        this.setState({ enteredMail: e.target.value })
    }

    handlePasswordChange = (e) => {
        this.setState({ enteredPassword: e.target.value })

    }
    render() {
        return (
            <MyContext.Consumer>

                {value =>
                    (
                        <>
                            {
                                console.log(this.state)
                            }
                            <Navbar expand="lg">
                                <Navbar.Brand href="#home"><Image src={Logo} /></Navbar.Brand>
                                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                                <Navbar.Collapse id="basic-navbar-nav">
                                    <Form inline className="SignIn_Form" onSubmit={this.handleSubmit(value.state.users)}>
                                        <Form.Group className="mr-2" >
                                            <Form.Control type="email" placeholder="Enter email" onChange={this.handleEmailChange} value={this.state.enteredMail} />
                                        </Form.Group>
                                        <Form.Group className="mr-2" >
                                            <Form.Control type="password" placeholder="Password" onChange={this.handlePasswordChange} value={this.state.enteredPassword} />
                                        </Form.Group>
                                        <Button variant="primary" type="submit" className="mr-2 SignIn_form-btn">
                                            Sign in
                        </Button>
                                        <Form.Text style={{ color: "red" }}>*{this.state.enteredDataValidation}</Form.Text>
                                    </Form>
                                    <Button variant="primary" className="mr-2 SignIn_form-btn">Sign in as admin</Button>
                                </Navbar.Collapse>
                            </Navbar>
                        </>
                    )
                }
            </MyContext.Consumer>
        )
    }
}

export default SignIn;
