import React from 'react';
import books from '../../books';
import { Dropdown, Row, Container, Col, Card, Button } from 'react-bootstrap';
// import Book from './Book';
import './Book.css';
class BookDetails extends React.Component {
    render() {
        const id = this.props.match.params.id;

        let Book = books.find((b) => {
            return (b.id === +id);
        });
        console.log(Book);

        return (
            <>
                <Container className="detailedCard">
                    <Row>
                        <Col md="1"></Col>
                        <Col md="3">
                            <Card style={{ width: '15rem' , height:'20rem' }}>
                                <Card.Img variant="top" src={Book.cover} />
                            </Card>;
                  </Col>
                        <Col md="8">
                            <Card style={{ width: '100%', border: 'none' }}>
                                <Card.Body>
                                    <Card.Title>{Book.title}</Card.Title>
                                    <Card.Subtitle className="mb-2 text-muted">By {Book.author}</Card.Subtitle>
                                    <Card.Text className="fontStyle">
                                      {Book.description}
    </Card.Text>
                                    <Card.Text >{Book.pages} Page </Card.Text>
                                    <Card.Text >{Book.category}</Card.Text>
                                </Card.Body>
                            </Card>
                            <h1 class="text-center" alt="Simple">Simple Pure CSS Star Rating Widget Bootstrap 4</h1>
 
<div class="container">
        <div class="starrating risingstar d-flex justify-content-center flex-row-reverse">
            <input type="radio" id="star5" name="rating" value="5" /><label for="star5" title="5 star">5</label>
            <input type="radio" id="star4" name="rating" value="4" /><label for="star4" title="4 star">4</label>
            <input type="radio" id="star3" name="rating" value="3" /><label for="star3" title="3 star">3</label>
            <input type="radio" id="star2" name="rating" value="2" /><label for="star2" title="2 star">2</label>
            <input type="radio" id="star1" name="rating" value="1" /><label for="star1" title="1 star">1</label>
        </div>
  </div>	
                  </Col>
                    </Row>
                </Container>
            </>
        )

    }
}
export default BookDetails;
