import React from 'react';
import { Card, Col,Modal,Button,Form } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


class EditCard extends React.Component {
    constructor(props, context) {
        super(props, context);

        this.handleShow = this.handleShow.bind(this);
        this.handleClose = this.handleClose.bind(this);

        this.state = {
            show: false,
            smShow: false,
        };
    }

    handleClose() {
        this.setState({ show: false });
    }

    handleShow() {
        this.setState({ show: true });
    }
    render() {
        let smClose = () => this.setState({ smShow: false });
        return (
            <>
                <Col key={this.props.id} className="m-3">
                    <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={this.props.cover} style={{ height: '20rem' }} />
                        <Card.Body>
                            <Card.Title>{this.props.title}
                                <Card.Text className="float-right">
                                    <FontAwesomeIcon className="mr-3" icon="edit" onClick={this.handleShow} />
                                    <FontAwesomeIcon icon="trash-alt" onClick={() => this.setState({ smShow: true })} />
                                </Card.Text>
                            </Card.Title>

                        </Card.Body>
                    </Card>
                </Col>

                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Edit Book</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                    <Form>
  <Form.Group >
    <Form.Label>Title</Form.Label>
    <Form.Control type="text" placeholder={this.props.title} />
  </Form.Group>
  <Form.Group >
    <Form.Label>Description</Form.Label>
    <Form.Control as="textarea" rows="3" placeholder={this.props.description} />
  </Form.Group>
</Form>;











                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                       </Button>
                        <Button variant="primary" onClick={this.handleClose}>
                            Save Changes
                       </Button>
                    </Modal.Footer>
                </Modal>
                <Modal
          size="sm"
          show={this.state.smShow}
          onHide={smClose}
          aria-labelledby="example-modal-sizes-title-sm"
        >
          <Modal.Header closeButton>
            <Modal.Title id="example-modal-sizes-title-sm">
              Delete
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
              Book Deleted!
          </Modal.Body>
        </Modal>
            </>

        );
    }


}

export default EditCard;
