    }
    wantToRead=(e)=>
    {

    }
    render() {
        const data = {
            columns: [
                {
                    label: 'Id',
                    field: 'id',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'Cover',
                    field: 'cover',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'Title',
                    field: 'title',
                    sort: 'asc',
                    width: 270
                },
                {
                    label: 'Author',
                    field: 'author',
                    sort: 'asc',
                    width: 200
                },
                {
                    label: 'Category',
                    field: 'category',
                    sort: 'asc',
                    width: 100
                },
                {
                    label: 'Rating',
                    field: 'rating',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'Pages',
                    field: 'pages',
                    sort: 'asc',
                    width: 100
                }
            ],
            rows: this.state.books

        }

        return (
            <>
                <UserNavBar />

                <div className="sideMenu">
                    {/* <Dropdown.Menu show> */}
                    <Dropdown.Header>Bookshelves</Dropdown.Header>
                    <Dropdown.Item eventKey="2" onClick={this.Allbooks}>All</Dropdown.Item>
                    <Dropdown.Item eventKey="3" onClick={this.Read} >Read</Dropdown.Item>
                    <Dropdown.Item eventKey="3" onClick={this.currentlyReading}>Currently Reading</Dropdown.Item>
                    <Dropdown.Item eventKey="3" onClick={this.Allbooks.wantToRead}>Want To Read</Dropdown.Item>

                    {/* </Dropdown.Menu> */}
                    <div className="spliterline"></div>
                </div>
                <div className="rightside">
                    <MDBDataTable
                        // small
                        data={data}
                    />

                </div>

            </>
        )
    }
}
export default UserHome;
