import React, { Component } from 'react'
import { Row, Col, Card, Form, Modal, Button } from 'react-bootstrap';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';


export default class AddCategory extends Component {
    constructor(props) {
        super(props);

        this.handleShow = this.handleShow.bind(this);
        this.handleClose = this.handleClose.bind(this);
        this.handleChange = this.handleChange.bind(this)
        this.state = {
            show: false,
            Name: ''
        };
    }

    handleClose() {
        this.setState({ show: false });
    }

    handleShow() {
        this.setState({ show: true });
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
        console.log("catnamet ", this.state[e.target.name])
    }

    render() {
        return (
            <>
                <Row>
                    <Col key="0" className="m-3">
                        <Card style={{ width: '18rem' }} onClick={this.handleShow}>
                            <Card.Body>
                                <Card.Title>Add Category
                            <FontAwesomeIcon className="float-right" icon="plus" />
                                </Card.Title>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
                <Row>
                    <Modal show={this.state.show} onHide={this.handleClose}>
                        <Modal.Header>
                            <Modal.Title>Add Category</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>

                            <Form>
                                <Form.Group >
                                    <Form.Label>Title</Form.Label>
                                    <Form.Control type="text" name="Name" value={this.state.Name} onChange={this.handleChange} />
                                </Form.Group>
                                
                            </Form>

                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={this.handleClose}>
                                Close
                       </Button>
                            <Button variant="primary" onClick={this.handleClose}>
                                Add
                       </Button>
                        </Modal.Footer>
                    </Modal>
                </Row>
            </>
        )
    }
}
