import React from 'react';
import { Form } from 'react-bootstrap';
import { Button } from 'react-bootstrap';

class SignUp extends React.PureComponent {
    state ={
        FName: '',
        LName: '',
        email:'',
        password: '',
        repeatedPassword: '',
        image: ''
    }
    handleSubmit= (e)=>
    {
      e.preventDefault();
    }
    handleChange=(e)=>
    {
       key=e.target.name;
       value= e.target.value;
       console.log(key,value);
       this.setState({[key]: value});
    }
    render() {
        return (
            <div style={{ height: '400px' }}>
                <Form className='SignUp_form' onSubmit={this.handleSubmit}>
                    <Form.Text className="text-muted">
                    New Here? Create a free account!
                    </Form.Text>
                    <Form.Group>
                    <Form.Control type="text" placeholder="First name" value={this.state.FName} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group>
                    <Form.Control type="text" placeholder="Last name" value={this.state.LName} onChange={this.handleChange}/>
                    </Form.Group>

                    <Form.Group>
                        <Form.Control type="email" placeholder="Enter email" value={this.state.email} onChange={this.handleChange}/>
                    </Form.Group>

                    <Form.Group >
                        <Form.Control type="password" placeholder="Password" value={this.state.password} onChange={this.handleChange}/>
                    </Form.Group>
                    <Form.Group >
                        <Form.Control type="password" placeholder="Retype Password" value={this.state.repeatedPassword} onChange={this.handleChange}/>
                    </Form.Group>
                    <Form.Group style={{display:'flex'}}>
                        <Form.Control type="file" placeholder="upload Image" className="ImageUpload" value={this.state.image} onChange={this.handleChange}/>
                        <Button variant="primary" type="submit" className='SignIn_form-btn'>
                        upload image
                       </Button>
                    </Form.Group>
                    <Button variant="primary" type="submit" className='SignUp_form-btn'>
                        Sign up
                    </Button>
                </Form>
            </div>
        )
    }
}

export default SignUp;
