import React from 'react';
import EditCard from './edit-card';
import Books from '../../Books';
import { Row, Col, Card, Form, Modal,Button } from 'react-bootstrap';

class AdminBooksList extends React.Component {
    constructor(props, context) {
        super(props, context);

        this.handleShow = this.handleShow.bind(this);
        this.handleClose = this.handleClose.bind(this);

        this.state = {
            show: false,
        };
    }
    handleClose() {
        this.setState({ show: false });
    }

    handleShow() {
        this.setState({ show: true });
    }

    render() {
        return (
            <Row>
                <Col key="0" className="m-3">
                    <Card style={{ width: '18rem' }} onClick={this.handleShow}>
                        <Card.Img style={{ height: '20rem' }} variant="top" src="data:image/png;base64," />
                        <Card.Body>
                            <Card.Title>Add Book
                            </Card.Title>
                        </Card.Body>
                    </Card>
                </Col>

                {Books.map(b => <EditCard id={b.id} cover={b.cover} title={b.title} description={b.description}
                    author={b.author} category={b.category} pages={b.pages} />)}

                <Modal key={Math.random()} show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header>
                        <Modal.Title>Add Book</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        <Form>
                            <Form.Group >
                                <Form.Label>Title</Form.Label>
                                <Form.Control type="text" />
                            </Form.Group>
                            <Form.Group >
                                <Form.Label>Author</Form.Label>
                                <Form.Control type="text" />
                            </Form.Group>
                            <Form.Group >
                                <Form.Label>Category</Form.Label>
                                <Form.Control type="text" />
                            </Form.Group>
                            <Form.Group >
                                <Form.Label>Pages</Form.Label>
                                <Form.Control type="number" />
                            </Form.Group>
                            <Form.Group >
                                <Form.Label>Description</Form.Label>
                                <Form.Control as="textarea" rows="3" />
                            </Form.Group>
                        </Form>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                       </Button>
                        <Button variant="primary" onClick={this.handleClose}>
                            Add
                       </Button>
                    </Modal.Footer>
                </Modal>
            </Row>


        );
    }
}

export default AdminBooksList;